{
  "username": "newadmin",
  "password": "password123",
  "theme": "ocean",
  "logoUrl": "https://example.com/logo.png",
  "backgroundUrl": "https://example.com/background.jpg",
  "welcomeMessage": "مرحباً بكم في مطعمنا\nنقدم أفضل الأطباق",
  "contactMessage": "للطلب اتصل على\n0123456789"
}

// Required fields:
// - username: unique username (string)
// - password: user password (string)
// - theme: one of "ocean", "sunset", "forest", "royal", "rose"
//
// Optional fields:
// - logoUrl: URL to logo image (string or empty)
// - backgroundUrl: URL to background image (string or empty)
// - welcomeMessage: multi-line welcome message (supports \n for new lines)
// - contactMessage: multi-line contact message (supports \n for new lines)

{
  "username": "updated_username",
  "password": "new_password_directly",
  "theme": "sunset",
  "logoUrl": "https://example.com/new-logo.png",
  "backgroundUrl": "",
  "welcomeMessage": "رسالة ترحيب محدثة\nسطر ثاني",
  "contactMessage": "رسالة تواصل محدثة"
}

// Developer PATCH - Direct modification without restrictions
// - No current password required
// - Any field can be updated directly
// - Send only the fields you want to update
// - Empty string "" removes the value
//
// Available fields:
// - username: change username
// - password: change password directly (no old password needed)
// - theme: one of "ocean", "sunset", "forest", "royal", "rose"
// - logoUrl: update or remove logo
// - backgroundUrl: update or remove background
// - welcomeMessage: update or remove welcome message
// - contactMessage: update or remove contact message
//
// Examples:
// Change password only: {"password": "new_pass"}
// Remove logo: {"logoUrl": ""}
// Change theme only: {"theme": "royal"}

{
  "username": "samakawy",
  "password": "password123"
}

// Login to get session token
// Required fields:
// - username: admin username
// - password: admin password
//
// Response includes:
// - token: session token (valid for 24 hours)
// - admin: admin data (without password)
//
// The session token will be automatically saved to environment variable

{
  "username": "updated_username",
  "theme": "royal",
  "logoUrl": "https://example.com/my-logo.png",
  "backgroundUrl": "https://example.com/my-bg.jpg",
  "welcomeMessage": "مرحباً بكم\nفي مطعمنا",
  "contactMessage": "اتصل بنا\n0123456789",
  "currentPassword": "old_password",
  "newPassword": "new_password"
}

// Update your own profile (ownership verified)
// Optional fields (send only what you want to update):
// - username: change username
// - theme: one of "ocean", "sunset", "forest", "royal", "rose"
// - logoUrl: update logo (empty string "" to remove)
// - backgroundUrl: update background (empty string "" to remove)
// - welcomeMessage: update welcome message
// - contactMessage: update contact message
// - newPassword: new password
// - currentPassword: REQUIRED only if changing password
//
// Password change:
// If you send newPassword, you MUST send currentPassword
// Otherwise currentPassword is not needed
//
// Examples:
// Change theme only: {"theme": "sunset"}
// Remove logo: {"logoUrl": ""}
// Change password: {"currentPassword": "old", "newPassword": "new"}

{
  "name": "المشروبات",
  "adminId": "{{CURRENT_ADMIN_ID}}"
}

// Create a new menu list
// Required fields:
// - name: list name (e.g., "المشروبات", "الأطباق الرئيسية")
// - adminId: your admin ID (must match your session)
//
// Note: itemType is automatically set to "عنصر"

{
  "name": "المشروبات الساخنة"
}

// Update list name
// Required field:
// - name: new list name
//
// Note: Can only update your own lists

{
  "name": "قهوة عربية",
  "price": 15.50,
  "description": "قهوة عربية أصيلة\nمحضرة بعناية من أجود أنواع البن\nتقدم ساخنة مع التمر",
  "listId": "list_id_here"
}

// Create a new menu item
// Required fields:
// - name: item name
// - price: item price (number)
// - listId: ID of the list this item belongs to
//
// Optional fields:
// - description: multi-line description
//   * First line will be displayed in BOLD
//   * Remaining lines in normal font
//   * Use \n for new lines
//
// Note: Can only add items to your own lists

{
  "name": "قهوة عربية ممتازة",
  "price": 18.00,
  "description": "قهوة عربية فاخرة\nمن أجود أنواع البن البرازيلي\nتقدم مع التمر والشوكولاتة",
  "listId": "new_list_id_here"
}

// Update menu item
// Optional fields (send only what you want to update):
// - name: update item name
// - price: update price
// - description: update description (empty string "" to remove)
// - listId: move to different list (must be your own list)
//
// Note: Can only update your own items
